import { useState } from 'react';
import Images from './Sliderdata';
import './SliderOne.css'

function SliderOne(children) {
    const [activeIndex, setActiveIndex] = useState(0);
    const slideNext = () => {
    setActiveIndex((val) => {
      if (val >= children.length - 1) {
        return 0;
      } else {
        return val + 1;
      }
    });
  };
    return (

        <div className="Slider_container">
            
            {children.map((img,index)=> {
                return(
                <div key={index} className={'Slider_item slider_item_active'+ (activeIndex + 1)}  >
                    {img}
            </div>
                )
            })}
            <button onClick={slideNext}>{'<'}</button>
                    </div>
    )
};
export default SliderOne;
