import { useState } from 'react';
import Images from './Sliderdata';
import './SliderOne.css'

function SliderOne() {
    const [activeIndex, setActiveIndex] = useState(0);
     // Function to go to the next slide
    const goToNextSlide = () => {
        setActiveIndex((prevIndex) => (prevIndex + 1) % Images.length);
    };

    // Function to go to the previous slide
    const goToPrevSlide = () => {
        setActiveIndex((prevIndex) => (prevIndex - 1 + Images.length) % Images.length);
    };
    return (
        <div className="Slider_container">
            <button className="slider_button slider_button_prev" onClick={goToPrevSlide}>{'<'}</button>
        
            {Images.map((data,index)=> {
                let positionClass = '';
                if(index === activeIndex)
                { positionClass = 'is-active'; }
                if(index === (activeIndex - 1 + Images.length)% Images.length)
                { positionClass = 'is-prev'; }
                if(index === (activeIndex + 1)% Images.length)
                { positionClass = 'is-next'; }
                return(
                <div key={index} className={'Slider_item slider_item_active'+ (activeIndex + 1)}  >
                    {img}
            </div>
                )
            })}
            <button className='slider_button slider_button_next' onClick={goToNextSlide}>{'>'}</button>
                    </div>
    )
};
export default SliderOne;
